// @flow
import * as React from "react";

// type assignment
import type MovieTypes from "../Types";

type Props = {
  item: MovieTypes
};

export default function InfoRow({ item, index }: Props) {
  if (item.poster !== "N/A") {
    return (
      <div className="column is-one-third-desktop is-half-mobile is-half-tablet">
        <img src={item.poster} alt={item.title} />
        <h2>{item.title}</h2>
      </div>
    );
  }
}
