// @flow
import * as React from "react";

// type assignment
import type MovieTypes from "../Types";

type Props = {
  item: MovieTypes
};

export default function InfoRow({ item, index }: Props) {
  return (
    <div className="column is-one-third-desktop is-half-mobile is-half-tablet">
      <figure class="image">
        <img
          src={
            item.poster || "https://bulma.io/images/placeholders/128x128.png"
          }
          alt={item.title || "No Title"}
        />
      </figure>
      <h2>{item.title}</h2>
    </div>
  );
}
